windows = [{"name": "Windows 10", "description": "Windows 10 is a series of operating systems developed by Microsoft and released as part of its Windows NT family of operating systems. It is the successor to Windows 8.1, released nearly two years earlier, and was released to manufacturing on July 15, 2015, and broadly released for the general public on July 29, 2015."},
           {"name": "Windows XP", "description": "Windows XP is an operating system produced by Microsoft as part of the Windows NT family of operating systems. It was the successor to both Windows 2000 for professional users and Windows Me for home users. It was released to manufacturing on August 24, 2001, and broadly released for retail sale on October 25, 2001."},
           {"name": "Windows 7", "description": "Windows 7 is an operating system that was produced by Microsoft and released as part of the Windows NT family of operating systems. It was released to manufacturing on July 22, 2009, and became generally available on October 22, 2009. It is the successor to Windows Vista, released nearly three years earlier."}]
linux = [{"name": "Arch Linux", "description": "Arch Linux is a Linux distribution for computers with x86-64 processors. Arch Linux adheres to the KISS principle and is focused on simplicity, modernity, pragmatism, user centrality, and versatility. Arch Linux also powers Compass!"},
         {"name": "Gentoo", "description": "Gentoo Linux is a Linux distribution built using the Portage package management system. Unlike a binary software distribution, the source code is compiled locally according to the user's preferences and is often optimized for the specific type of computer."},
         {"name": "Linux Mint", "description": "Linux Mint is a community-driven Linux distribution based on Ubuntu, bundled with a variety of free and open-source applications. It can provide full out-of-the-box multimedia support for those who choose to include proprietary software such as multimedia codecs. The Linux Mint project was created by Clément Lefèbvre and is actively maintained by the Linux Mint Team and community."},
         {"name": "Fedora", "description": "Fedora is a Linux distribution developed by the community-supported Fedora Project which is sponsored primarily by Red Hat, a subsidiary of IBM, with additional support from other companies. Fedora contains software distributed under various free and open-source licenses and aims to be on the leading edge of free technologies."},
         {"name": "Manjaro", "description": """Manjaro is a free and open-source Linux distribution based on the Arch Linux operating system. Manjaro has a focus on user-friendliness and accessibility, and the system itself is designed to work fully "straight out of the box" with its variety of pre-installed software. It features a rolling release update model and uses Pacman as its package manager."""}]
